sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Get yesterday's time and date
var less24Hours = new GlideDateTime(rightNow.addDaysUTC(-1));

// Query the database for NeedIt Task records with Due date field values older
// than the current time less 24 hours, and newer than the current time. 
// Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var nearlyOverdueNITasks = new GlideRecord('x_58872_needit_needit_task');
nearlyOverdueNITasks.addQuery('due_date', '>', rightNow);
nearlyOverdueNITasks.addQuery('due_date', '<=', less24Hours);
nearlyOverdueNITasks.addQuery('state', '!=', 3);

nearlyOverdueNITasks.query();

// Write a log message for each nearly overdue NeedIt Task Record
while (nearlyOverdueNITasks.next()) {
    gs.info("Nearly overdue NeedIt Task record = " + nearlyOverdueNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', nearlyOverdueNITasks, nearlyOverdueNITasks.number, gs.getUserName());
}
